SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM member m LEFT OUTER JOIN member_detail d ON m.mem_num = d.mem_num
WHERE d.mem_name LIKE '%' || #{keyword} || '%' OR d.mem_num LIKE '%' || #{keyword} || '%'
ORDER BY d.mem_regdate DESC NULLS LAST
ORDER BY d.mem_name ASC, d.mem_regdate DESC NULLS LAST
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT b.*, mem_name, mem_phone FROM (SELECT item_rnum, item_title, item_index, mem_num, item_start, item_end FROM item_detail d LEFT OUTER JOIN item i ON d.item_num = i.item_num)b LEFT OUTER JOIN member_detail m ON b.mem_num = m.mem_num
WHERE b.item_title LIKE '%' || #{keyword} || '%'
ORDER BY b.item_start DESC NULLS LAST
ORDER BY m.mem_name ASC, b.item_start DESC NULLS LAST
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT b.*, mem_name, mem_phone FROM (SELECT item_rnum, item_title, item_index, mem_num, item_start, item_end FROM item_detail d LEFT OUTER JOIN item i ON d.item_num = i.item_num)b LEFT OUTER JOIN member_detail m ON b.mem_num = m.mem_num WHERE b.item_end IS NULL
AND b.item_title LIKE '%' || #{keyword} || '%'
ORDER BY b.item_start DESC NULLS LAST
ORDER BY b.item_start ASC NULLS LAST
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT b.*, ticket_name FROM (SELECT p.mem_num, mem_name, pay_plan, TO_CHAR(pay_price, '999,999') pay_price, TO_CHAR(pay_date, 'yyyy-MM-dd HH24:MI:SS') pay_date, ticket_num FROM pay p LEFT OUTER JOIN member_detail d ON p.mem_num = d.mem_num)b LEFT OUTER JOIN ticket t ON b.ticket_num = t.ticket_num
WHERE b.mem_name LIKE '%' || #{keyword} || '%' OR b.mem_num LIKE '%' || #{keyword} || '%'
ORDER BY b.pay_date DESC NULLS LAST
ORDER BY b.mem_name ASC, b.pay_date DESC NULLS LAST
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT * FROM (SELECT talk_num, t.mem_num, mem_name, message, TO_CHAR(chat_date, 'yyyy-MM-dd HH24:MI:SS') chat_date FROM talk t LEFT OUTER JOIN member_detail d ON t.mem_num = d.mem_num)b LEFT OUTER JOIN member m ON b.mem_num = m.mem_num WHERE m.mem_auth != 9
AND (b.mem_name LIKE '%' || #{keyword} || '%' OR b.mem_num LIKE '%' || #{keyword} || '%')
ORDER BY b.chat_date DESC, b.talk_num DESC NULLS LAST
ORDER BY b.mem_name ASC, b.talk_num DESC NULLS LAST
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

